class Solution {
public:    
    unordered_map<string, unordered_set<int>> connected;

    vector<vector<string>> findLadders(string beginWord, string endWord, vector<string>& wordList) {
        int target = distance(wordList.begin(), find(wordList.begin(), wordList.end(), endWord));
        if (beginWord.size() != endWord.size() || target == wordList.size()) {
            return {};
        }
        wordList.push_back(beginWord);
        for (int i = 0; i < wordList.size(); i++) {
            const auto& word = wordList[i];
            for (int c = 0; c < word.length(); c++) {
                auto codeword = word;
                codeword[c] = '#';
                connected[codeword].insert(i);
            }
        }
        vector<unordered_set<int>> adj (wordList.size());
        for (const auto& [codeword, ccp] : connected) {
            for (auto i : ccp) {
                for (auto j : ccp) {
                    if (i != j) {
                        adj[i].insert(j);
                    }
                }
            }
        }
        vector<int> dist (wordList.size(), 10000000);
        vector<vector<int>> prev (wordList.size());
        int source = wordList.size() - 1;
        dist[source] = 0;
        priority_queue<pair<int, int>, vector<pair<int, int>>, greater<>> pq;
        pq.emplace(0, source);
        while (!pq.empty()) {
            auto [d, u] = pq.top();
            pq.pop();
            for (auto v : adj[u]) {
                auto alt = d + 1;
                if (alt < dist[v]) {
                    dist[v] = alt;
                    prev[v] = {u};
                    pq.emplace(alt, v);
                } else if (alt == dist[v]) {
                    prev[v].push_back(u);
                }
            }
        }
        vector<vector<string>> res;
        vector<int> curr_seq;
        curr_seq.push_back(target);
        get_sequence(target, prev, res, wordList, curr_seq, dist[target] + 1);
        return res;
    }

    void get_sequence(int curr, const vector<vector<int>>& prev, vector<vector<string>>& seqs,
                      const vector<string>& wordList, vector<int>& curr_seq, int len) {
        if (curr_seq.size() == len) {
            auto rev_seq = curr_seq;
            reverse(rev_seq.begin(), rev_seq.end());
            vector<string> word_seq;
            for (auto idx : rev_seq) {
                word_seq.push_back(wordList[idx]);
            }
            seqs.push_back(move(word_seq));
            return;
        }
        for (auto neigh : prev[curr]) {
            curr_seq.push_back(neigh);
            get_sequence(neigh, prev, seqs, wordList, curr_seq, len);
            curr_seq.pop_back();
        }
    }

};

