import java.util.*;

class Solution {
    List<List<String>> res;
    public List<List<String>> findLadders(String beginWord, String endWord, List<String> wordList) {
        this.res = new LinkedList<>();
        Set<String> dict = new HashSet<>(wordList);
        if(!dict.contains(endWord)) {
            return res;
        }
        dict.remove(beginWord);
        dict.remove(endWord);
        LinkedList<String> q = new LinkedList<>();
        q.offer(beginWord);
        Map<String, Integer> steps = new HashMap<>();
        Map<String, List<String>> parents = new HashMap<>();
        steps.put(beginWord, 1);
        int step = 1;
        boolean found = false;
        while (!q.isEmpty() && !found) {
            step++;
            int size = q.size();
            while (size-- > 0) {
                String p = q.poll();
                // System.out.println(p);
                char[] chars = p.toCharArray();
                for(int i = 0; i < chars.length; i++) {
                    char c = chars[i];
                    for(char j = 'a'; j <= 'z'; j++ ) {
                        chars[i] = j;
                        String w = String.valueOf(chars);
                        if (w.equals(endWord)) {
                            // steps.put(w, step);
                            System.out.println("end : " + w);
                            parents.putIfAbsent(w, new LinkedList<>());
                            parents.get(w).add(p);
                            found = true;
                        } else {
                            if (steps.containsKey(w) && step == steps.get(w)) {
                                parents.get(w).add(p);
                            }
                        }
                        if (!dict.contains(w)) {
                            continue;
                        }
                        dict.remove(w);
                        parents.putIfAbsent(w, new LinkedList<>());
                        parents.get(w).add(p);
                        steps.put(w, step);
                        q.offer(w);
                    }
                    chars[i] = c;
                }
            }
        }
        if (found) {
            LinkedList<String> list = new LinkedList<>();
            list.add(endWord);
            dfs(parents, list, beginWord, endWord);
        }
        return res;
    }
    public void dfs(Map<String, List<String>> parents, LinkedList<String> list, String beginWord, String cur) {
        if (cur.equals(beginWord)) {
            LinkedList<String> tmp = new LinkedList<>(list);
            Collections.reverse(tmp);
            res.add(tmp);
            return;
        }
        for (String parent : parents.get(cur)) {
            list.add(parent);
            dfs(parents, list, beginWord, parent);
            list.removeLast();
        }
    }
}
