class Solution {
    vector<vector<string>> res;
    unordered_map<string, unordered_set<string>> graph;
    unordered_map<string, vector<string>> graph_back;

    void dfs(vector<string> &cur, string &target, int n) {
        auto s = cur.back();
        if (cur.size() == n+1) {
            if (s == target) {
                res.push_back(vector<string>(cur.rbegin(), cur.rend()));
            }
            return;
        }

        for (auto &next : graph_back[s]) {
            cur.push_back(next);
            dfs(cur, target, n);
            cur.pop_back();
        }
    }
public:
    vector<vector<string>> findLadders(string beginWord, string endWord, vector<string>& wordList) {
        unordered_set<string> set0(wordList.begin(), wordList.end()), visited;

        // build a graph
        wordList.push_back(beginWord);
        for (auto word : wordList) {
            for (int j = 0; j < word.size(); j++) {
                for (char c = 'a'; c <= 'z'; c++) {
                    if (c != word[j]) {
                        auto next = word;
                        next[j] = c;
                        if (set0.count(next)) {
                            graph[word].insert(next);
                            graph[next].insert(word);
                        }
                    }
                }
            }
        }

        //BFS to find min length
        queue<string> que;
        que.push(beginWord);
        visited.insert(beginWord);
        int step = -1;
        bool found = false;
        while(que.size()) {
            step++;
            int size = que.size();
            auto previsited = visited;
            for (int i = 0; i < size; i++) {
                auto cur = que.front();
                que.pop();

                if (cur == endWord) {
                    found = true;
                    break;
                }

                for (auto &next : graph[cur]) {
                    if (!previsited.count(next)) { //previsited does not include nodes visited at this layer
                        graph_back[next].push_back(cur);
                    }
                    if (!visited.count(next)) {
                        visited.insert(next);
                        que.push(next);
                    }
                }
            }
            if (found) {
                break;
            }
        }

        // backtracking to found all path with length = step
        vector<string> cur{endWord};
        dfs(cur, beginWord, step);
        return res;
    }
};

