/**
 * @param {string} beginWord
 * @param {string} endWord
 * @param {string[]} wordList
 * @return {string[][]}
 */
var findLadders = function(beginWord, endWord, wordList) {
  const _getNextWords = (currWord) => {
    const words = wordList.indexOf(beginWord) === -1 ? [...wordList, beginWord] : wordList;
    return words.filter((word) => {
      if (word === currWord) return false;
      let diff = 0;
      for (let i = 0; i < word.length; i += 1) {
        if (diff > 1) {
          return false;
        }
        if (word[i] !== currWord[i]) {
          diff += 1;
        }
      }
      return diff === 1;
    });
  };

  // calculate distance map for words bottom up from endWord to beginWord
  const _bfs = (endWord) => {
    const queue = [endWord];

    while (queue.length > 0) {
      const currWord = queue.shift();
      if (currWord === beginWord) {
        break;
      }
      const nextWords = _getNextWords(currWord);
      const currDist = distance.get(currWord);
      for (let next of nextWords) {
        if (!distance.has(next)) {
          distance.set(next, currDist + 1);
          queue.push(next);
        }
      }
    }
  };

  const _dfs = (word, path) => {
    if (word === endWord) {
      results.push(path);
      return;
    }
    for (let next of _getNextWords(word)) {
      if (distance.has(next) && distance.get(next) + 1 === distance.get(word)) {
        _dfs(next, [...path, next]);
      }
    }
  };

  const results = [];
  const distance = new Map();
  distance.set(endWord, 0);
  _bfs(endWord);
  _dfs(beginWord, [beginWord]);

  return results;
};
