        m, n = len(G), len(G[0])
        ans = [[0] * n for _ in range(m)]
        for r in range(m):
            for c in range(n):
                delta, c2 = divmod(c + k, n)
                ans[(r + delta) % m][c2] = G[r][c]

        return ans
