class Solution {
public:
    vector<vector<int>> shiftGrid(vector<vector<int>>& grid, int k) 
    {
        int r=grid.size(), c=grid[0].size();
        vector<vector<int>> ans(r, vector<int>(c));

        for(int i=0; i<r; i++)
        {
            for(int j=0; j<c; j++)
            {
                //for k=1 new column is (j+1), similarly for k=k new column is (j+k) 
                //(j+k)%c for maintain total no. of columns
                int newj = (j+k)%c;

                //divide (j+k) with c to maintain no. of columns and move to next row
                //(i+(j+k)/c)%r for maintain no. of rows and if all rows end
                //we start from beginning
                int newi = (i + (j+k)/c)%r;

                //put value into ans with new i, j
                ans[newi][newj] = grid[i][j];
            }
        }
        return ans;
    }
};
