        k = (size - k % size) % size;
        List<List<Integer>> res = new ArrayList<>();
        for (int i = 0; i < m; i++) {
            List<Integer> tmp = new ArrayList<>();
            for (int j = 0; j < n; j++) {
                int x = k / n, y = k % n;
                tmp.add(grid[x][y]);
                k = (k + 1) % size;
            }
            res.add(tmp);
        }
        return res;
    }
}
