pub fn shift_grid(mut A: Vec<Vec<i32>>, k: i32) -> Vec<Vec<i32>> {
	let (m, n) = (A.len(), A[0].len());
	let mut R = vec![vec![0; n]; m];
	let k = k as usize;
	for i in 0..m {
		for j in 0..n {
			let y = (j + k) % n;           // j shift right k times
			let x = (i + (j + k) / n) % m; // i shift down (j + k) / n times
			R[x][y] = A[i][j];
		}
	}
	R
}
