class Solution {
public:
    vector<vector<int>> shiftGrid(vector<vector<int>>& grid, int k) {
        int m = grid.size(), n = grid[0].size();
        while(k--){
            // Take a vector of length m with initial value of 0 to store the last column
            vector<int> tmp(m,0);
            //Store the values of last column in "tmp" for later 
            for(int i = 0; i < m; i++){
                tmp[i] = grid[i][n-1];
                cout << tmp[i];
            }
            //iterate from last column to the second column
            for(int i = 0; i < m; i++){
                for(int j = n - 1; j >= 1 ; j--){
                     // Store the values of previous column in the current column
                    grid[i][j] = grid[i][j - 1];
                }
            }
            // Move the element at grid[m - 1][n - 1] to grid[0][0]
            grid[0][0] = tmp[m-1];
            // Move the rest of the values in the first column
            for(int i = 1; i < m; i++){
                grid[i][0] = tmp[i-1];
            }
        }
        return grid;
    }
};
