var shiftGrid = function(grid, k) {
	const M = grid.length;
	const N = grid[0].length;
	let res = Array.from({length: M}, (v, k) => new Int16Array(N));
	for (let i = 0; i < M; i++) {
		for (let j = 0; j < N; j++){
			let newCol = (j + k) % N;
			let newRow = (i + (j+k)/N) % M;
			res[~~newRow][newCol] = grid[i][j];
		}
	}
	return res;
}
