public class Solution {
    public IList<IList<int>> ShiftGrid(int[][] grid, int k) {
        var n = grid.Length;
        var m = grid[0].Length;
        var values = grid.SelectMany(x => x);
        var mod = k % (n * m);  

        var flattened = values.Skip(n * m - mod).Concat(values.Take(n * m - mod));
        return Enumerable
            .Range(0, n)
            .Select(x => flattened.Skip(x * m).Take(m).ToList())
            .ToList<IList<int>>();
    }
}
```

