class Solution {
public:
    vector<vector<int>> shiftGrid(vector<vector<int>>& grid, int k) {
        int col = grid[0].size();
        int n = grid.size() * col;

        k = k % n;
        if (k == 0) return grid;

        while (k-- > 0) {
            int prev = grid[0][0];

            for (int i = 0; i < n-1; i++) {
                int idx = (i + 1) % n;
                int tmp = grid[idx / col][idx % col];
                grid[idx / col][idx % col] = prev;
                prev = tmp;
            }

            grid[0][0] = prev;
        }

        return grid;
    }
};
```

