var shiftGrid = function(grid, k) {
    const m = grid.length;
    const n = grid[0].length;

    const move = (grid) => {
        for (let i = 0; i < m; i++) {
            const current = [];
            for (let j = 0; j < n; j++) {
                current[j] = grid[i][j];
                grid[i][j] = j === 0 ? grid[i][n - 1] : current[j - 1];
            }
        }

        const current = [];
        for (let i = 0; i < m; i++) {
            current[i] = grid[i][0];
            grid[i][0] = i === 0 ? grid[m - 1][0] : current[i - 1];
        }
    }

    for (let i = 1; i <= k; i++) {
        move(grid);
    }

    return grid;
};
