const shiftGrid = function (grid, k) {
	const m = grid.length,
		n = grid[0].length,
		nums = grid.flat();
	k = k % nums.length;
	nums.splice(0, 0, ...nums.slice(nums.length - k, nums.length));
	nums.splice(-k, k);
	let i = 0;
	while (nums.length) grid[i++] = nums.splice(0, n);
	return grid;
};
