// Using .flat() + offset and refilling the grid in-place.
var shiftGrid = function(grid, k) {
	const M = grid.length, N = grid[0].length;
	let flat = grid.flat();
	if (k > flat.length) k = k % flat.length;
	let shft = flat.splice(flat.length - k, k);
	flat.unshift(...shft);
	for (let i = 0; i < M; i++) {
		for (let j = 0; j < N; j++) {
			grid[i][j] = flat[i*N + j];
		}
	}
	return grid;
};
```

