class Solution:
    def shiftGrid(self, grid: List[List[int]], k: int) -> List[List[int]]:
        for i in range(k):
			#take last element in the matrix and move it to first position
            grid[0].insert(0, grid[-1].pop())

			# for each row after 1st row, pop last element in previous row, then add it to the beginning of current row
            for j in range(1,len(grid)):
                grid[j].insert(0, grid[j-1].pop())
        return grid
