public class FindElements {
    TreeNode root;

    public FindElements(TreeNode root) {
        this.root = root;
        Queue<TreeNode> q = new LinkedList<>();

        q.offer(root);
        root.val = 0;

        while (!q.isEmpty()){

            int queueSize = q.size();
            for (int i = 0; i < queueSize; i++) {
                TreeNode temp = q.peek();

                if (temp.left != null){
                    q.offer(temp.left);
                    temp.left.val = 2* temp.val +1;
                }

                if (temp.right!= null) {
                    q.offer(temp.right);
                    temp.right.val = 2* temp.val +2;
                }
                q.poll();
            }

        }
    }

    public boolean find(int target) {

        return helper(root, target);

    }
    public boolean helper(TreeNode rootRec, int target){
        if (rootRec == null) return false;

        if (rootRec.val == target) return true;

        boolean bool1 =helper(rootRec.left, target);
        if (bool1) return true;
        boolean bool2 = helper(rootRec.right, target);
        if (bool2) return true;

        return false;
    }
}




