class FindElements {
public:

    TreeNode* treeNode;
    unordered_map<int,bool> valueMap;

    void recoverTree(TreeNode* root) {

        queue<TreeNode*> bfsQ;
        TreeNode *temp;
        bfsQ.push(root);
        root->val = 0;
        valueMap[root->val] = true;

        do {

            temp = bfsQ.front();
            if(temp->left != NULL) {

                bfsQ.push(temp->left);
                temp->left->val = bfsQ.front()->val * 2 + 1;
                valueMap[temp->left->val] = true;

