            if(temp->right != NULL) {

                bfsQ.push(temp->right);
                temp->right->val = bfsQ.front()->val * 2 + 2;
                valueMap[temp->right->val] = true;
            }

            bfsQ.pop();

        }while(!bfsQ.empty());


    }

    FindElements(TreeNode* root) {

        treeNode = root;
        recoverTree(treeNode);
    }

    bool find(int target) {

        if(valueMap.find(target) != valueMap.end())
            return true;
        else
            return false;
    }
};
