class FindElements {
    TreeNode root;

    public FindElements(TreeNode root) {
        this.root = root;
    }

    public boolean find(int target) {
        target++;
        List<Integer> path = new ArrayList<>();
        while (target > 1) {
            path.add(0, target);
            target /= 2;
        }

        TreeNode cur = root;
        for (int num : path) {
            if (num % 2 == 0) {
                cur = cur.left;
            } else {
                cur = cur.right;
            }
            if (cur == null) {
                return false;
            }
        }

        return true;
    }
}

