class FindElements {
public:
    FindElements(TreeNode* root) {
        r = root;
        if (!root) return;
        build(root, 0);
    }

    bool find(int target) {
        stack<bool> st;
        int t = target;
        while (t > 0) {
            if (t % 2) {
                st.push(true);
                t = (t - 1) / 2;
            } else {
                st.push(false);
                t = t / 2 - 1;
            }
        }
        TreeNode *cur = r;
        while (!st.empty()) {
            if (!cur) return false;
            if (st.top()) {
                cur = cur->left;
            } else {
                cur = cur->right;
            }
            st.pop();
        }
        if (cur && cur->val == target) return true;
        return false;
    }

private:
    TreeNode *r;

    void build(TreeNode* root, int val) {
        root->val = val;
        int n = 2 * val + 1;
        if (root->left) build(root->left, n);
        if (root->right) build(root->right, n + 1);
    }
};
