/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class FindElements {
TreeNode* myroot;
public:
    FindElements(TreeNode* root) {
        root->val = 0;
        myroot = root;
        ContaminateTree(myroot);
    }

    bool find(int target) {
        bool found = false;
        dfs_find(myroot , target , found);
        return found;
    };

    void ContaminateTree(TreeNode* root){
        // Base Case (if current node is leaf node)
        if(root->left == nullptr && root->right == nullptr){
            return ;
        }

        // processing
        if(root->left != nullptr){
            root->left->val = 2*root->val + 1;
        }
        if(root->right != nullptr){
            root->right->val = 2*root->val + 2;
        }

        // recursive calls
        // left
        if(root->left != nullptr){
            ContaminateTree(root->left);
        }
        // right
        if(root->right != nullptr){
            ContaminateTree(root->right);
        }

        return ;
    };
    void dfs_find(TreeNode* root , int target , bool &found){
        // Base Case (if current node is leaf node)
        if(root->left == nullptr && root->right == nullptr){
            if(root->val == target){
                found = true;
            }
            return ;
        }

        // processing
        if(root->val == target){
            found = true;
        }

        if(found == true){
            return;
        }

        // recursive calls
        // left
        if(root->left != nullptr){
            dfs_find(root->left , target , found);
        }
        // right
        if(root->right != nullptr){
            dfs_find(root->right , target , found);
        }

        return ;
    }
};

