```
/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class FindElements {
public:
    unordered_set<int>st;
    int maxi=0;
    void p(TreeNode* root, int val) {
        if(!root) return;
        root->val=val;
        if(!root->left && !root->right) {st.insert(val); maxi=max(maxi,val);}
        p(root->left,2*val+1);
        p(root->right,2*val+2);
    }
    FindElements(TreeNode* root) {
        p(root,0);
    }

    bool find(int t) {
        queue<int>q;
        q.push(t);
        while(!q.empty()) {
            int k=q.front(); q.pop();
            if(st.find(k)!=st.end()) return 1;
            if((2*k+1) > maxi) continue;
            if((2*k+1) <=maxi) q.push(2*k+1);
            if((2*k+2) <= maxi) q.push(2*k+2);
        }
        return 0;
    }
};

