public IList<IList<int>> ShiftGrid(int[][] grid, int k) => grid
    // flatten the grid to a list
    .SelectMany(x => x)
    // we need 2 lists and we are going to select a window of numbers
    .Concat(grid.SelectMany(x => x))
    // calculate the offset of charcters to skip
    .Skip(grid.Length * grid[0].Length - k % (grid.Length * grid[0].Length))
    // chunk the flat list into rows
    .Chunk(grid[0].Length)
    // the number of rows should be unchanged
    .Take(grid.Length)
    // we need a type compatiable with IList
    .ToArray();
