public class Solution {
    public IList<IList<int>> ShiftGrid(int[][] grid, int k) {

        int m = grid.Length, n = grid[0].Length, size = m * n;

        k %= size;
        reverse(grid, 0, size - 1);
        reverse(grid, 0, k - 1);
        reverse(grid, k, size - 1);

        int[][] res = new int[m][];
        for(int i = 0; i < m; i++)
            res[i] = new int[n];

        for(int i = 0; i < m; i++)
        {
            for(int j = 0; j < n; j++)
                res[i][j] = grid[i][j];
        }

        return res;
    }

    private void reverse(int[][] grid, int left, int right)
    {
        int m = grid.Length, n = grid[0].Length;

        while(left < right)
        {
            int r = left / n, c = left % n;
            int nr = right / n, nc = right % n;

            int tmp = grid[r][c];
            grid[r][c] = grid[nr][nc];
            grid[nr][nc] = tmp;
            left++;
            right--;
        }
    }
}
