class Solution {
    public List<List<Integer>> shiftGrid(int[][] grid, int k) {

        // column width of the grid
        int colWidth = grid[0].length;

        // row height of the grid
        int rowHeight = grid.length;

        // totalElements in the grid
        int modulo = colWidth * rowHeight;

        //temp flat one dimensional array to store the values, when they have finally shifted by K times
        int[] finalIndexes = new int[colWidth * rowHeight];

        //go thru every element in the grid
        for(int i = 0; i < grid.length; i++){
            for(int j = 0; j < grid[0].length; j++){

                // final index within a grid is calculated as currentIndex 
// (imagine it is not a 2-d grid but a 1-d array so that element in 2nd row 2nd column is at index 4 in the 1-d array... ), 
// we just add K to it and then divide by total number of elements 
// and whatever is there a reminder then it would be the final location
                int finalIndex = ((i * colWidth) + j + k ) % modulo;

                //assign a value to it's final index
                finalIndexes[finalIndex] = grid[i][j];
            }
        }


        // the below part is just a boilerplate for returning the value in the desired output format
        List<List<Integer>> resultList = new ArrayList<>();

        // since this is a list of list , then the outer list would contain sublists equal to the height of the original grid
        for(int i = 0; i < rowHeight; i++){
            resultList.add(new ArrayList<>());
        }

        int counter = 0;
        int rowCount = -1;

        for(int i = 0; i < finalIndexes.length; i++){
            if(i % colWidth == 0){
                rowCount++; // increment row count if all the elements are filled in the previous row
            }    
            //add to the row where it belongs    
            resultList.get(rowCount).add(finalIndexes[i]);
        }

        // the answer
        return resultList;
    }
}
