class Solution {
public:
    vector<vector<int>> shiftGrid(vector<vector<int>>& a, int k) {
        int n = a.size(), m = a[0].size();
        vector<int> b(n*m);

        for(int i=0; i<n; i++)
        {
            for(int j=0; j<m; j++)
            {
                b[i*m + j] = a[i][j];
            }
        }

        k %= (n*m);

        rotate(b.begin(), b.end()-k, b.end());

        for(int i=0; i<n*m; i++)
        {
            a[i/m][i%m] = b[i];
        }

        return a;
    }
};
