class FindElements
{
public:
    TreeNode *root;
    FindElements(TreeNode *root2)
    {
        root = root2;
    }

    bool find(int tar)
    {
        stack<int> st;
        while (tar > 0)
        {
            int x = 0;
            if ((tar & 1) == 1)
            {
                x = 1;
            }
            tar--;
            tar >>= 1;
            st.push(x);
        }
        TreeNode *temp = root;
        while (!st.empty())
        {

            if (st.top() == 1)
            {
                if (temp->left)
                {
                    temp = temp->left;
                }
                else
                {
                    return false;
                }
            }
            else
            {
                if (temp->right)
                {
                    temp = temp->right;
                }
                else
                {
                    return false;
                }
            }
            st.pop();
        }
        return true;
    }
};

```

