```java	static class FindElements {

		TreeNode root;

		public FindElements(TreeNode r) {
			if (r == null) {
				this.root = null;
			} else {
				this.root = new TreeNode(0);
				recover(this.root, r);
			}

		}

		private void recover(TreeNode root, TreeNode referenceRoot) {
			if (referenceRoot.left != null) {
				root.left = new TreeNode(root.val * 2 + 1);
				recover(root.left, referenceRoot.left);
			}
			if (referenceRoot.right != null) {
				root.right = new TreeNode(root.val * 2 + 2);
				recover(root.right, referenceRoot.right);
			}

		}

		public boolean find(int target) {
			if (target < 0) {
				return false;
			}
			if (target == 0) {
				return true;
			}
			TreeNode curr = root;
			if (curr == null) {
				return false;
			}
			Stack<Boolean> stack = new Stack<Boolean>();
			int tmp = target;

			while (tmp > 0) {
				if (tmp % 2 == 0) {
					// even
					stack.push(true);
					tmp = (tmp - 2) / 2;
				} else {
					// odd
					stack.push(false);
					tmp = (tmp - 1) / 2;
				}
			}

			while (!stack.isEmpty()) {
				if (stack.pop()) {
					// right
					if (curr.right == null) {
						return false;
					} else {
						if (curr.right.val == target) {
							return true;
						}
						curr = curr.right;
					}

				} else {
					// left
					if (curr.left == null) {
						return false;
					} else {
						if (curr.left.val == target) {
							return true;
						}
						curr = curr.left;
					}
				}
			}
			return false;
		}
	}`
