class FindElements {
    private TreeNode root;

    public FindElements(TreeNode root) {
        this.root = root;
    }

    public boolean find(int x) {
        int mask = 1;
        while (x != 0) {
            mask <<= 1;
            mask |= (x & 1);
            x = (x - 1) >> 1;
        }
        TreeNode node = root;
        while (node != null) {
            if (mask == 1) return true;
            node = (mask & 1) == 1 ? node.left : node.right;
            mask >>= 1;
        }
        return false;
    }
}
