/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class FindElements {
    private:
    unordered_set<int>values;  // Using set to remove duplicacy.
public:
    FindElements(TreeNode* root) {
        if(root->val!=NULL)
        {
            root->val=0; //  So as the statement is asking you if root is not null then make it null.
            recoverTree(root);
        }
    }
    void recoverTree(TreeNode* root)
    {
        values.insert(root->val); // insert values inside set
        if(root->left!=NULL)
        {
          root->left->val=2*root->val+1;
          recoverTree(root->left);
        }
        if(root->right!=NULL)
        {
            root->right->val=2*root->val+2;
            recoverTree(root->right);
        }
    }

    bool find(int target) {
        return values.count(target)>0;
    }
};

