class FindElements {
public:
    TreeNode* root = nullptr;
    FindElements(TreeNode* root) {
        this->root = root;
    }
    bool find(int target) {
        vector<bool> path;
        int targetCopy = target;
        while(targetCopy) {
            if (targetCopy % 2 == 0) {
                path.push_back(true);
                targetCopy -= 2;
            } else {
                path.push_back(false);
                targetCopy -= 1;
            }
            targetCopy /= 2;
        }
        TreeNode* head = root;
        for(auto it = path.rbegin(); it != path.rend(); it++) {
            if (head == nullptr) {
                return false;
            }
            if (*it) {
                head = head->right;
            } else {
                head = head->left;
            }
        }
        return head ? true : false;
    }
};
```

