    int solve(int i,vector<int>& nums,int r){
        if(i == nums.size()){
            if(r == 0){
                return 0;
            }
            return -1000000;
        }

        int op1 = nums[i] + solve(i+1,nums,(r+nums[i])%3);
        int op2 = solve(i+1,nums,r);
        return max(op1,op2);
    }
	 int maxSumDivThree(vector<int>& nums) {
	     return solve(0,nums,0);
	 }
};
