class Solution {
public:
    int n;
    int dp[40001][3];
    int f(int i,int sum,vector<int> &v){
        if(i==n)return 0;

        if(dp[i][sum]!=-1)return dp[i][sum];

        int ans=0;
        if((sum+f(i+1,sum,v))%3==0){
            ans=f(i+1,sum,v);
        }
        if((sum+v[i]+f(i+1,(sum+v[i])%3,v))%3==0){
            ans=max(ans,v[i]+f(i+1,(sum+v[i])%3,v));
        }

        return dp[i][sum]=ans;

    }

    int maxSumDivThree(vector<int>& v) {
        n=v.size();
        memset(dp,-1,sizeof dp);
        return f(0,0,v);
    }
};
