class Solution {
    public static int [][] t;
    public int maxSumDivThree(int[] nums) {
        int n=nums.length;
        t=new int[n+1][3];
        for(int [] i:t) Arrays.fill(i,-1);
        return solve(nums,0,0);
    }
    public static int solve(int [] nums,int i,int curr){
        if(i>=nums.length){
            if(curr==0) return 0;
            return -10000;
        }
        if(t[i][curr]!=-1) return t[i][curr];
        int take=nums[i]+solve(nums,i+1,(curr+nums[i])%3);
        int left=solve(nums,i+1,curr);
        return t[i][curr]=Math.max(take,left);
    }
}
