class Solution {
public:
    int maxSumDivThree(vector<int>& nums) {
        int n = nums.size(), sum = 0;

        for(auto i : nums) sum += i;
        if(sum % 3 == 0) return sum;

        vector<int>v;
        for(auto i : nums){
            if(i%3) v.push_back(i);
        }

        sort(v.begin(),v.end());
        v.push_back(1e9+7);

        int curr = 0 , l = 0;

        for(int i=0; i<v.size(); i++){
            while(curr < v[i]){
                if(sum % 3 == curr % 3) return sum - curr;
                curr += v[l];
                l++;
            }

            if(sum % 3 == v[i] % 3) return sum - v[i];
        }

        return 0;
    }
};
