class Solution {
    public int maxSumDivThree(int[] nums) {
        int[][] dp = new int[3][nums.length + 1];
        for(int i = 1; i <= nums.length; i++) {
            int curNum = nums[i - 1];
            for(int k = 0; k <= 2; k++) { // for 3 remainders possible i.e. 0, 1, 2
                int sum = dp[k][i - 1] + curNum; // add cur number with previous sum for all remainders each.
                int newRemainder = sum % 3;
                dp[newRemainder][i] = Math.max(dp[newRemainder][i], sum); // hold sum for new remainder 
                dp[k][i] = Math.max(dp[k][i - 1], dp[k][i]); // check if we can pick max sum from previous location
            }

        }

        return dp[0][nums.length];
    }
}
