from typing import List

class Solution:
    def maxSumDivThree(self, nums: List[int]) -> int:
        # key: remainder - 0, 1, or 2
        # value: maximum sum that can be divide by 3 with the remainer
        max_dict = {0: 0}

        for n in nums:
            previous_max = list(max_dict.values())
            for p in previous_max:
                new_sum = p + n
                remainder = new_sum % 3
                max_dict[remainder] = max(max_dict.get(remainder, new_sum), new_sum)

        return max_dict[0]
