class Solution {
    public int maxSumDivThree(int[] nums) {
        if (nums == null || nums.length == 0) return 0;
        int[] dp = new int[3]; // index for each of the type of remainder we can get when divide a number by 3
        for (int i = 0; i < nums.length;i++) {

            //3 possible remainders are 0,1,2
            // array indices indicate the remainders
            // add the current number to each of the value in dp[index]
            // take the remainder of the new sum against those 3 numbers 
            int threes = dp[0] + nums[i];
            int ones = dp[1] + nums[i];
            int twos = dp[2] + nums[i];
            // if the existing value against sum%3 is lesser then copy that number.
            if(threes > dp[threes%3]) dp[threes%3] = threes;
            if(ones > dp[ones%3]) dp[ones%3] = ones;
            if(twos > dp[twos%3]) dp[twos%3] = twos;
        }
        //finally we are asked to produce the max sum divisible by 3.
        return dp[0];
        //if we were asked to produce max sum with remainder 1 then dp[1] or 
        //if we are asked about 2 remainder than dp[2] will be the values to be returned.
    }

}
