class Solution {
    int recurr(vector<int>&nums, int i, int len, int sum, vector<vector<int>>&dp,int mod)
    {

        if(i >= len)
        {

           if(mod%3 == 0)
           {
               return 0;
           }
            return INT_MIN;
        }
       if(dp[i][mod] != 0)
        {

           return dp[i][mod];
       }

       int a = nums[i] + recurr(nums, i+1, len, sum+nums[i], dp, (mod+nums[i])%3);
       int b = recurr(nums, i+1, len, sum,dp,mod);
       return dp[i][mod] = max(a,b);

    }
public:

    int maxSumDivThree(vector<int>& nums) {
        vector<vector<int>>dp(nums.size(), vector<int>(3,0));
       int a = recurr(nums,0,nums.size(),0,dp,0);
        return a;
    }

};
