class Solution {
public:
    int maxSumDivThree(vector<int>& nums) {
        int dp[3];
        int sum=0;

        dp[1]=10000;
        dp[2]=10000;

        for(int i:nums){
            if(i%3)
            {
                if(i%3==1) dp[2]=min(dp[2],dp[1]+i), dp[1]=min(dp[1],i);
                else       dp[1]=min(dp[1],dp[2]+i), dp[2]=min(dp[2],i);
            }
            sum+=i;
        }

        return sum-dp[sum%3];
