import math

class Solution:
    def maxSumDivThree(self, nums: List[int]) -> int:
        max_sum = sum(nums)
        if max_sum % 3:
            # if the total sum has a remainder the remainder can be neutralized by subtracting the smallest number
            # with the same remainder or the sum of two smallest numbers with a different remainder
            if max_sum % 3 == 1:
                # search for x % 3 == 1 or two minimum numbers such that x % 3 == 2
                min_mod1   = math.inf
                min_mod2   = [math.inf, math.inf]
                for num in nums:
                    mod = num % 3
                    if mod == 1 and num < min_mod1:
                        min_mod1 = num
                    elif mod == 2 and num < max(min_mod2):
                        min_mod2[min_mod2[0] < min_mod2[1]] = num

                max_sum -= min(min_mod1, sum(min_mod2))
            else:
                min_mod2 = math.inf
                min_mod1 = [math.inf, math.inf]
                for num in nums:
                    mod = num % 3
                    if mod == 1 and num < max(min_mod1):
                        min_mod1[min_mod1[0] < min_mod1[1]] = num
                    elif mod == 2 and num < min_mod2:
                        min_mod2 = num
                max_sum -= min(min_mod2, sum(min_mod1))

        return max_sum
