    def maxSumDivThree(self, nums):
        seen = {
            0: [],
            1: [],
            2: []
        }

        for num in nums:
            seen[num % 3].append(num)

        s = sum(seen[0] + seen[1] + seen[2])
        remainderOne = sorted(seen[1])[:2]
        remainderTwo = sorted(seen[2])[:2]

        if s % 3 == 2:
            if len(remainderOne) < 2 and len(remainderTwo) < 1:
                return 0
            elif len(remainderOne) < 2:
                return s - remainderTwo[0]
            elif len(remainderTwo) < 1:
                return s - sum(remainderOne)
            return s - min(sum(remainderOne), remainderTwo[0])

        elif s % 3 == 1:
            if len(remainderOne) < 1 and len(remainderTwo) < 2:
                return 0
            elif len(remainderOne) < 1:
                return s - sum(remainderTwo)
            elif len(remainderTwo) < 2:
                return s - remainderOne[0]
            return s - min(remainderOne[0], sum(remainderTwo))

        return s

