    def maxSumDivThree(self, nums: List[int]) -> int:
        D, Max = {}, 0
        Max1, Max2, Max3 =  0, 0 , 0
        for i in range(len(nums)):
            D[0], D[1], D[2] = 0, 0, 0
            D[nums[i]%3] = nums[i]
            if not Max1 == 0:
