class Solution {
public:
    int maxSumDivThree(vector<int>& nums) {
        priority_queue<int,vector<int>,greater<int>> pq1;
        priority_queue<int,vector<int>,greater<int>> pq2;

        int res = 0;
        for (int i = 0; i < nums.size(); ++i) {
            res += nums[i];

            if (nums[i]%3 == 1)
                pq1.push(nums[i]);
            else if (nums[i]%3 == 2)
                pq2.push(nums[i]);
        }

        int var11 = 100000;
        int var12 = 100000;
        int var21 = 100000;
        int var22 = 100000;
        if (pq1.size() > 0) { var11 = pq1.top(); pq1.pop(); }
        if (pq1.size() > 0) { var12 = pq1.top(); }
        if (pq2.size() > 0) { var21 = pq2.top(); pq2.pop(); }
        if (pq2.size() > 0) { var22 = pq2.top(); }

        if (res % 3 == 1)
            res -= min(var11,var21+var22);
        else if (res % 3 == 2)
            res -= min(var21,var11+var12);

        return res;
    }
};
