const maxSumDivThree = function (nums) {
  const sum = nums.reduce((a, c) => a + c);

  if (sum % 3 === 0) return sum;

  nums.sort((a, b) => a - b)

  let numsSum = 0;
  let singleNumDiff = 0;
  let multiNumSumDiff = 0;

  for (const num of nums) {
    if ((sum - num) % 3 === 0) {
      singleNumDiff = sum - num;
      break;
    }
  }

  for (const num of nums) {
    if (num % 3) numsSum += num;
    if ((sum - numsSum) % 3 === 0) {
      multiNumSumDiff = sum - numsSum;
      break;
    }
  }

  return Math.max(singleNumDiff, multiNumSumDiff);
};


