        vector<vector<pair<int, int>>> dp(n + 1, vector<pair<int, int>>(5, {-1, -1}));

        for (int i = 0; i < n; i++)
            b[i] = a[i] % 3;

        return f(0, 0, a, dp).second;
    }
};
