class Solution:
    def maxSumDivThree(self, nums: List[int]) -> int:
        arr=[0,0,0]
        for i in range(len(nums)):
            val=[0,0,0]
            for j in range(3):
                s=(nums[i]+arr[j])
                val[s%3]=max(val[s%3],s)
                val[j]=max(arr[j],val[j])
            arr=val
        return arr[0]

