class Solution:
    def shiftGrid(self, grid: List[List[int]], k: int) -> List[List[int]]:
        def shift(grid):
            for i in range(len(grid) - 1):
                grid[i + 1].insert(0, grid[i].pop())
            grid[0].insert(0, grid[-1].pop())
            return grid
        for i in range(k):
            shift(grid)
        return grid
