class FindElements {
public:
  FindElements(TreeNode* root) {
    traverse(root, 0);
  }

  bool find(int target) {
    return valueSet.count(target);
  }

private:
  unordered_set<int> valueSet;

  void traverse(TreeNode* node, int val) {
    if (!node)
      return;

    valueSet.insert(val);

    traverse(node->left, val * 2 + 1);
    traverse(node->right, val * 2 + 2);
  }
};
